mapper.select:
SELECT S.STORAGE_LOCATION, P.PRODUCT_NO, P.PRODUCT_NAME, C.CATEGORY_NAME, P.COLOR, P.PRODUCT_SIZE, I.QUANTITY FROM INVENTORY I JOIN STORAGE S ON I.STORAGE_NO = S.STORAGE_NO JOIN PRODUCT P ON I.PRODUCT_NO = P.PRODUCT_NO JOIN CATEGORY C ON P.CATEGORY_NO = C.CATEGORY_NO WHERE I.STORE_ID = #{storeId}
AND I.STORAGE_NO = #{storageNo}
AND LOWER(P.PRODUCT_NAME) LIKE '%' || #{keyword} || '%'
ORDER BY P.PRODUCT_NO ASC OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

SELECT COUNT(*) FROM INVENTORY I JOIN STORAGE S ON I.STORAGE_NO = S.STORAGE_NO JOIN PRODUCT P ON I.PRODUCT_NO = P.PRODUCT_NO JOIN CATEGORY C ON P.CATEGORY_NO = C.CATEGORY_NO WHERE I.STORE_ID = #{storeId}
AND I.STORAGE_NO = #{storageNo}
AND LOWER(P.PRODUCT_NAME) LIKE '%' || #{keyword} || '%'

SELECT *
FROM INVENTORY
WHERE STORAGE_NO = #{storageNo}
AND   PRODUCT_NO = #{productNo}
AND STORE_ID = #{storeId}

mapper.update:
UPDATE INVENTORY
SET QUANTITY = QUANTITY + #{quantity}
WHERE STORAGE_NO = #{storageNo}
AND   PRODUCT_NO = #{productNo}
AND STORE_ID = #{storeId}

UPDATE INVENTORY
SET QUANTITY = QUANTITY - #{quantity}
WHERE STORAGE_NO = #{storageNo}
AND   PRODUCT_NO = #{productNo}
