mapper.select:
SELECT P.PRODUCT_NO, P.PRODUCT_NAME, P.CATEGORY_NO, C.CATEGORY_NAME, P.COLOR, P.PRODUCT_SIZE, P.STOCK_IN_PRICE, P.STOCK_OUT_PRICE, P.STATUS FROM PRODUCT P JOIN CATEGORY C ON P.CATEGORY_NO = C.CATEGORY_NO
AND P.STATUS = #{status}
AND P.CATEGORY_NO = #{categoryNo}
AND LOWER(P.PRODUCT_NAME) LIKE '%' || #{keyword} || '%'
ORDER BY P.PRODUCT_NO ASC OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

SELECT COUNT(*)
FROM PRODUCT P
JOIN CATEGORY C ON P.CATEGORY_NO = C.CATEGORY_NO



        AND P.STATUS = #{status}




        AND P.CATEGORY_NO = #{categoryNo}




        AND LOWER(P.PRODUCT_NAME) LIKE '%' || #{keyword} || '%'

mapper.update:
UPDATE CLIENT
SET CLIENT_NAME = #{clientName},
    CLIENT_CEO = #{clientCeo},
    CLIENT_PHONE = #{clientPhone},
    CLIENT_ADDRESS = #{clientAddress}
WHERE CLIENT_ID = #{clientId}

UPDATE CLIENT
SET STATUS = 'N'
WHERE CLIENT_ID = #{clientId}
